import React, { Component } from "react";
import Carousel from 'react-bootstrap/Carousel';
import { TableCard } from "../TableCard";
import { BookingApi } from "../../services/booking/BookingApi";
import Container from "react-bootstrap/Container";

type SliderState = {
    restaurants: any[]
};

export class Slider extends Component<{}, SliderState> {
    constructor(props: any) {
        super(props);

        this.state = {
            restaurants: []
        };
    }

    componentDidMount() {
        this.getRestaurants();
    }


    private async getRestaurants() {
        const bookingApi = new BookingApi('');
        const restaurants = await bookingApi.listRestaurants().then(response => {
            return response.json().then(data => {
                return data;
            });
        });

        const newRestaurants = [];
        for (const restaurant of restaurants) {
            const tables = await this.getTables(restaurant.guid);

            if (tables.length > 0) {
                restaurant.tables = tables;
                newRestaurants.push(restaurant);
            }
        }

        this.setState({ restaurants: newRestaurants });

    }

    private async getTables(restaurantGuid: string) {
        const bookingApi = new BookingApi('');

        return bookingApi.listTables(restaurantGuid).then(response => {
            return response.json().then(response => {
                return response;
            });
        });
    }

    render() {
        const restaurants = this.state.restaurants;
        return(
            <Container>
                {restaurants.map((restaurant: any) =>

                    <Carousel className="p-3 container" controls={false} key={restaurant.guid}>
                        {restaurant.tables.map((table: any) =>
                            <Carousel.Item key={table.guid}>
                                <TableCard guid={table.guid} positionName={table.positionName} />
                            </Carousel.Item>
                        )};
                    </Carousel>
                )}
            </Container>
        );
    }
}
