import React, { Component } from "react";
import Card from 'react-bootstrap/Card';
import Button from "react-bootstrap/Button";
import Carousel from 'react-bootstrap/Carousel';
import { BookingApi } from "../../services/booking/BookingApi";

type TableCardProps = {
    restaurant: string,
    restaurantGuid: string
};

export class TableCard extends Component<TableCardProps, {}> {

    constructor(props: TableCardProps) {
        super(props);
    }

    componentDidMount() {
        this.getTables();
    }


    public getTables() {
        const bookingApi = new BookingApi('');
        bookingApi.listTables(this.props.restaurantGuid).then(response => {
            response.json().then(data => {
                this.setState({ restaurants: data.data });
            });
        });
    }


    render() {
        return(
            <Carousel.Item>
                <Card style={{ width: '18rem' }}>
                <Card.Img variant="top" src="holder.js/100px180" />
                    <Card.Body>
                        <Card.Title>Card Title</Card.Title>
                        <Card.Text>
                            Some quick example text to build on the card title and make up the bulk of
                            the card's content.
                        </Card.Text>
                        <Button variant="primary">Go somewhere</Button>
                    </Card.Body>
                </Card>
                <Carousel.Caption>
                    <h3>{restaurant.name}</h3>
                </Carousel.Caption>
            </Carousel.Item>
        );
    }
}
