import React, { Component, RefObject } from "react";
import Form from 'react-bootstrap/Form';
import Button from "react-bootstrap/Button";
import { BookingApi } from "../../services/booking/BookingApi";
import { GenericModal } from "../Modal";
import InputMoment from 'input-moment';
import moment from 'moment';
import './Calendar.css';

type ResponseData = {
    reservationCode?: string,
    message: string,
    status: string
};

type ReservationStates = {
    when: moment.Moment,
    showModal: boolean,
    response: ResponseData
};

type ReservationProps = {
    reservationGuid?: string,
    restaurantGuid: string,
    tableGuid: string
};

export class Reservation extends Component<ReservationProps, ReservationStates> {
    private genericModal: RefObject<GenericModal>;

    constructor(props: ReservationProps) {
        super(props);

        const now = moment();
        now.set({minute: 0});

        this.state = {
            when: now,
            showModal: false,
            response: {
                message: '',
                reservationCode: '',
                status: ''
            }
        }

        this.genericModal = React.createRef();

        this.handleDataChange = this.handleDataChange.bind(this);
        this.createReservation = this.createReservation.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleSave = this.handleSave.bind(this);
    }

    private handleDataChange(event: any) {
        const name = event.target.id;
        const value = event.target.value;
        const data = {
            [name]: value
        } as ReservationStates;

        this.setState(data);
    }

    private createReservation() {
        const bookingApi = new BookingApi('');
        const response = {
            message: 'Restaurant registered with success',
            status: 'success'
        }

        const userGuid = '123';

        const when = this.state.when.format('YYYY-MM-DD hh:mm:ss');

        bookingApi.createReservation(when, userGuid, this.props.restaurantGuid, this.props.tableGuid).then(result => {
            const allowedStatus = [200, 201];

            if (!allowedStatus.includes(result.status)) {
                result.json().then(response => {
                    this.setState({ showModal: true, response });
                });
            }
        }).catch(err => {
            response.status = 'Error';
            response.message = err;
        });

        this.setState({ showModal: true, response });

        this.genericModal.current?.openModal();
    }

    private handleChange(when: moment.Moment) {
        this.setState({ when });
    };

    private handleSave() {
        console.log('saved', this.state.when.format('llll'));
        this.createReservation();
    };

    render() {
        return(
            <div>
                <GenericModal ref={this.genericModal} message={this.state.response.message} status={this.state.response.status} />

                <InputMoment moment={this.state.moment}
                    onChange={this.handleChange}
                    onSave={this.handleSave}
                    minStep={60} // default
                    hourStep={1} // default
                    prevMonthIcon="ion-ios-arrow-left" // default
                    nextMonthIcon="ion-ios-arrow-right" // default
                />
            </div>
        );
    }
}
