import React, { Component, RefObject } from "react";
import Form from 'react-bootstrap/Form';
import Button from "react-bootstrap/Button";
import { Link } from 'react-router-dom';
import Container from "react-bootstrap/Container";
import { BookingApi } from "../../services/booking/BookingApi";
import { GenericModal } from "../Modal";

type ResponseData = {
    data?: string,
    message: string,
    status: string
};

type TableStates = {
    positionName: string,
    maxSeats: number,
    showModal: boolean,
    response: ResponseData
};

type TableProps = {
    restaurantGuid: string,
    tableGuid?: string
};

export class Table extends Component<TableProps, TableStates> {
    private genericModal: RefObject<GenericModal>;

    constructor(props: TableProps) {
        super(props);

        this.state = {
            positionName: '',
            maxSeats: 0,
            showModal: false,
            response: {
                message: '',
                data: '',
                status: ''
            }
        }

        this.genericModal = React.createRef();

        this.handleDataChange = this.handleDataChange.bind(this);
        this.addTable = this.addTable.bind(this);
    }

    private handleDataChange(event: any) {
        const name = event.target.id;
        const value = event.target.value;
        const data = {
            [name]: value
        } as TableStates;

        this.setState(data);
    }

    private addTable(event: any) {
        const bookingApi = new BookingApi('');
        const response = {
            message: 'Restaurant registered with success',
            status: 'success'
        }

        bookingApi.addTable(this.state.positionName, this.state.maxSeats, this.props.restaurantGuid).then(result => {
            const allowedStatus = [200, 201];

            if (!allowedStatus.includes(result.status)) {
                result.json().then(response => {
                    this.setState({ showModal: true, response });
                });
            }
        }).catch(err => {
            response.status = 'Error';
            response.message = err;
        });

        this.setState({ showModal: true, response });

        this.genericModal.current?.openModal();

        event.preventDefault();
    }

    render() {
        return(
            <Container>
                <nav>
                    <ol className="breadcrumb">
                        <li className="breadcrumb-item">
                            <a href="/">Home</a>
                        </li>
                        <Link className="breadcrumb-item" to="/manager">Manager restaurants</Link>
                        <Link className="breadcrumb-item" to={`/restaurants/${this.props.restaurantGuid}/list-tables`}>List tables</Link>
                        <li className="breadcrumb-item active">Edit table</li>
                    </ol>
                </nav>
                <Form onSubmit={this.addTable}>
                    <GenericModal ref={this.genericModal} message={this.state.response.message} status={this.state.response.status} />

                    <Button variant="primary" type="submit">Reserve</Button>
                </Form>
            </Container>
        );
    }
}
