import React, { Component } from "react";
import Carousel from 'react-bootstrap/Carousel';
import { TableCard } from "../TableCard";
import { BookingApi } from "../../services/booking/BookingApi";
import Container from "react-bootstrap/Container";

type SliderState = {
    restaurants: any[]
};

export class Slider extends Component<{}, SliderState> {
    constructor(props: any) {
        super(props);

        this.state = {
            restaurants: []
        };
    }

    componentDidMount() {
        this.getRestaurants();
    }


    public getRestaurants() {
        const bookingApi = new BookingApi('');
        bookingApi.listRestaurants().then(response => {
            response.json().then(data => {
                this.setState({ restaurants: data.data });
            });
        });
    }

    render() {
        const restaurants = this.state.restaurants;
        return(
            <Container>
                {restaurants.map((restaurant: any) =>

                    <Carousel className="p-3 container" controls={false} key={restaurant.guid}>
                            <TableCard restaurant={restaurant.name} restaurantGuid={restaurant.guid} />
                    </Carousel>
                )}
            </Container>
        );
    }
}
