import React, { Component } from "react";
import Card from 'react-bootstrap/Card';
import Button from "react-bootstrap/Button";
import Carousel from 'react-bootstrap/Carousel';
import { BookingApi } from "../../services/booking/BookingApi";

type TableCardStates = {
    tables: any[]
}

type TableCardProps = {
    restaurant: string,
    restaurantGuid: string
};

export class TableCard extends Component<TableCardProps, TableCardStates> {

    constructor(props: TableCardProps) {
        super(props);

        this.state = {
            tables: []
        };
    }

    componentDidMount() {
        this.getTables();
    }


    private getTables() {
        const bookingApi = new BookingApi('');
        bookingApi.listTables(this.props.restaurantGuid).then(response => {
            response.json().then(data => {
                this.setState({ tables: data.data });
            });
        });
    }


    render() {
        const tables = this.state.tables;

        return(
            <div>
                {tables.map((table: any) =>

                    <Carousel.Item key={table.guid}>
                        <Card style={{ width: '18rem' }}>
                        <Card.Img variant="top" src="holder.js/100px180" />
                            <Card.Body>
                                <Card.Title>{table.positionName}</Card.Title>
                                <Card.Text>
                                    Some quick example text to build on the card title and make up the bulk of
                                    the card's content.
                                </Card.Text>
                                <Button variant="primary">Go somewhere</Button>
                            </Card.Body>
                        </Card>
                        <Carousel.Caption>
                            <h3>{this.props.restaurant}</h3>
                        </Carousel.Caption>
                    </Carousel.Item>
                )}
            </div>
        );
    }
}
