import React, { Component } from "react";
import Button from "react-bootstrap/Button";
import Modal from 'react-bootstrap/Modal';
import { Redirect } from 'react-router-dom';

type ModalProps = {
    message: string,
    status: string
};

type ModalStates = {
    showModal: boolean,
    redirect: boolean
}

export class GenericModal extends Component<ModalProps, ModalStates> {
    constructor(props: ModalProps) {
        super(props);

        this.state = {
            showModal: false,
            redirect: false
        };

        this.openModal = this.openModal.bind(this);
        this.closeModal = this.closeModal.bind(this);
    }

    public openModal() {
        this.setState({ showModal: true });
    }

    public closeModal() {
        this.setState({ showModal: false, redirect: true });
    }

    render() {
        if (this.state.redirect) {
            setTimeout(() => {
                return <Redirect to='/list-restaurants' />
            }, 1000);
            return;
        }

        return(
            <Modal show={this.state.showModal} onHide={this.closeModal}>
                <Modal.Header className="alert-success" closeButton>
                    <Modal.Title style={{textTransform: 'capitalize'}}>{this.props.status}</Modal.Title>
                </Modal.Header>
                    <Modal.Body>{this.props.message}</Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={this.closeModal}>Ok</Button>
                </Modal.Footer>
            </Modal>
        );
    }
}
