import React, { Component } from "react";
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';
import { BookingApi } from "../../services/booking/BookingApi";

type ReservationListStates = {
    reservations: any[],
}

type ReservationListProps = {
    restaurantGuid: string,
    tableGuid: string
};

export class ReservationList extends Component<ReservationListProps, ReservationListStates> {

    constructor(props: ReservationListProps) {
        super(props);

        this.state = {
            reservations: []
        };
    }

    public componentDidMount() {
        this.getReservations();
    }

    private getReservations() {
        const bookingApi = new BookingApi('');
        bookingApi.listReservations(this.props.restaurantGuid, this.props.tableGuid).then(response => {
            response.json().then(data => {
                this.setState({ reservations: data.data });
            });
        });
    }

    render() {
        const reservations = this.state.reservations;

        return(
            <Table responsive>
                <thead>
                    <tr>
                    <th>Reservation code</th>
                    <th>Restaurant</th>
                    <th>Table</th>
                    <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {reservations.map((reservation: any) =>
                        <tr key={reservation.reservationCode}>
                            <td>{reservation.restaurant}</td>
                            <td>{reservation.table}</td>
                            <td>-</td>
                            <td>
                                <Button variant="outline-primary">Edit table</Button>
                                <Button variant="outline-primary">View reservations</Button>
                            </td>
                        </tr>
                    )}
                </tbody>
            </Table>
        );
    }
}
