import React, { Component } from "react";
import { BrowserRouter, NavLink } from 'react-router-dom';
import Table from 'react-bootstrap/Table';
import { BookingApi } from "../../services/booking/BookingApi";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Nav from 'react-bootstrap/Nav';
import { matchPath } from "react-router";

type BookingState = {
    restaurants: any[]
};

export class Booking extends Component<{}, BookingState> {
    constructor(props: any) {
        super(props);

        this.state = {
            restaurants: []
        };
    }

    componentDidMount() {
        this.getRestaurants();
    }



    public getRestaurants() {
        const bookingApi = new BookingApi('');
        bookingApi.listRestaurants().then(response => {
            response.json().then(data => {
                this.setState({ restaurants: data.data });
            });
        });
    }

    render() {
        const restaurants = this.state.restaurants;
        // const className = this.props.defa === 'booking' ? 'active' : '';
        const isFirstAccess = matchPath("/booking", {
            path: "/booking",
            exact: true,
            strict: false
          });

        return(
            <BrowserRouter>
                <Container>
                    <Row>
                        <Col sm={2}>
                        <Nav variant="pills" defaultActiveKey="/list-restaurants" className="flex-column">
                            <Nav.Link as={NavLink} active={isFirstAccess} activeClassName='is-active' to="/list-restaurants">Restaurants</Nav.Link>
                            <Nav.Link as={NavLink} to="/list-tables">Tables</Nav.Link>
                            <Nav.Link as={NavLink} to="/list-reservations">Reservations</Nav.Link>
                        </Nav>
                        </Col>
                        <Col sm={10}>
                            <Table responsive>
                                <thead>
                                    <tr>
                                    <th>Restaurant</th>
                                    <th>Business hours</th>
                                    <th>Actions</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {restaurants.map((restaurant: any) =>
                                        <tr>
                                            <td>{restaurant.name}</td>
                                            <td>From {restaurant.opensAt} to {restaurant.closesAt} </td>
                                            <td>{restaurant.guid} </td>
                                        </tr>
                                    )}
                                </tbody>
                            </Table>
                        </Col>
                    </Row>
                </Container>
            </BrowserRouter>
        );
    }
}
