import React, { Component } from "react";
import { BrowserRouter, NavLink, Switch, Route } from 'react-router-dom';
import Table from 'react-bootstrap/Table';
import { BookingApi } from "../../services/booking/BookingApi";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Nav from 'react-bootstrap/Nav';

type BookingState = {
    restaurants: any[],
    reservations: any[],
    tables: any[],
    isDefaultActive: boolean;
};

export class Booking extends Component<{}, BookingState> {
    constructor(props: any) {
        super(props);

        this.state = {
            restaurants: [],
            reservations: [],
            tables: [],
            isDefaultActive: true
        };
    }

    componentDidMount() {
        this.getRestaurants();
    }

    activeLink(isActive: boolean) {
        this.setState({ isDefaultActive: isActive });
    }


    public getRestaurants() {
        const bookingApi = new BookingApi('');
        bookingApi.listRestaurants().then(response => {
            response.json().then(data => {
                this.setState({ restaurants: data.data });
            });
        });

        bookingApi.listTables().then(response => {
            response.json().then(data => {
                this.setState({ tables: data.data });
            });
        });

        bookingApi.listReservations().then(response => {
            response.json().then(data => {
                this.setState({ reservations: data.data });
            });
        });
    }

    render() {
        const restaurants = this.state.restaurants;
        // const className = this.props.defa === 'booking' ? 'active' : '';
        return(
            <BrowserRouter>
                <Container>
                    <Row>
                        <Col sm={2}>
                        <Nav variant="pills" defaultActiveKey="/list-restaurants" className="flex-column">
                            <Nav.Link as={NavLink} active={this.state.isDefaultActive} to="/list-restaurants" onClick={() => this.activeLink(true)}>Restaurants</Nav.Link>
                            <Nav.Link as={NavLink} to="/list-tables" onClick={() => this.activeLink(false)} >Tables</Nav.Link>
                            <Nav.Link as={NavLink} to="/list-reservations" onClick={() => this.activeLink(false)} >Reservations</Nav.Link>
                        </Nav>
                        </Col>
                        <Col sm={10}>
                            <Switch>
                                <Route path={["/list-restaurants", "/booking"]}>
                                    <Table responsive>
                                        <thead>
                                            <tr>
                                            <th>Restaurant</th>
                                            <th>Business hours</th>
                                            <th>Actions</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {restaurants.map((restaurant: any) =>
                                                <tr key={restaurant.guid}>
                                                    <td>{restaurant.name}</td>
                                                    <td>From {restaurant.opensAt} to {restaurant.closesAt} </td>
                                                    <td>{restaurant.guid} </td>
                                                </tr>
                                            )}
                                        </tbody>
                                    </Table>
                                </Route>
                                <Route path="/list-tables">
                                    asdf
                                </Route>
                                <Route path="/list-reservations">
                                    123
                                </Route>
                            </Switch>
                        </Col>
                    </Row>
                </Container>
            </BrowserRouter>
        );
    }
}
