import React, { Component } from "react";
import Table from 'react-bootstrap/Table';
import { Link } from 'react-router-dom';
import Nav from 'react-bootstrap/Nav';
import Container from "react-bootstrap/Container";
import { BookingApi } from '../../services/booking/BookingApi';
import moment from 'moment';

type ReservationListStates = {
    reservations: any[],
}

type ReservationListProps = {
    restaurantGuid: string,
    tableGuid: string
};

export class ReservationList extends Component<ReservationListProps, ReservationListStates> {

    constructor(props: ReservationListProps) {
        super(props);

        this.state = {
            reservations: []
        };

        this.cancelReservation = this.cancelReservation.bind(this);
    }

    public componentDidMount() {
        this.getReservations();
    }

    private getReservations() {
        const bookingApi = new BookingApi('');
        bookingApi.listReservations(this.props.restaurantGuid, this.props.tableGuid).then(response => {
            response.json().then(data => {
                this.setState({ reservations: data.data });
            });
        });
    }

    private cancelReservation(event: any) {

    }

    render() {
        const reservations = this.state.reservations;

        return(
            <Container>
                <nav>
                    <ol className="breadcrumb">
                        <li className="breadcrumb-item">
                            <a href="/">Home</a>
                        </li>
                        <Link className="breadcrumb-item" to="/manager">Manager restaurants</Link>
                        <Link className="breadcrumb-item" to={`/restaurants/${this.props.restaurantGuid}/list-tables`}>List tables</Link>
                        <li className="breadcrumb-item active">List reservations</li>
                    </ol>
                </nav>
                <Table responsive>
                    <thead>
                        <tr>
                        <th>Reservation code</th>
                        <th>Date</th>
                        <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {reservations.map((reservation: any) => {
                            const date = moment(reservation.when);

                            return(<tr key={reservation.reservationCode}>
                                <td>{reservation.reservationCode}</td>
                                <td>{date.format('YYYY-MM-DD')} at {date.format('hh:mm A')}</td>
                                <td>
                                    <Nav>
                                        <Nav.Item>
                                            <Link className="btn btn-outline-primary" to={`/restaurants/${this.props.restaurantGuid}/tables/${this.props.tableGuid}/reservations/${reservation.reservationCode}`}>Edit reservation</Link>
                                            <Link className="btn btn-outline-primary" prevent to="/" onClick={(e) => this.cancelReservation(e)}>Cancel reservation</Link>
                                        </Nav.Item>
                                    </Nav>
                                </td>
                            </tr>)
                    })}
                    </tbody>
                </Table>
            </Container>
        );
    }
}
