import React, { Component } from "react";
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';
import { BookingApi } from "../../services/booking/BookingApi";

type TableListStates = {
    tables: any[],
}

type TableListProps = {
    restaurantGuid: string
};

export class TableList extends Component<TableListProps, TableListStates> {

    constructor(props: TableListProps) {
        super(props);

        this.state = {
            tables: []
        };
    }

    public componentDidMount() {
        this.getTables();
    }

    public getTables() {
        const bookingApi = new BookingApi('');
        bookingApi.listTables(this.props.restaurantGuid).then(response => {
            response.json().then(data => {
                this.setState({ tables: data.data });
            });
        });
    }

    render() {
        const tables = this.state.tables;

        return(
            <Table responsive>
                <thead>
                    <tr>
                    <th>Table position</th>
                    <th>Max seats</th>
                    <th>Availability</th>
                    <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {tables.map((table: any) =>
                        <tr key={table.guid}>
                            <td>{table.positionName}</td>
                            <td>{table.maxSeats}</td>
                            <td>-</td>
                            <td>
                                <Button variant="outline-primary">Edit table</Button>
                                <Button variant="outline-primary">View reservations</Button>
                            </td>
                        </tr>
                    )}
                </tbody>
            </Table>
        );
    }
}
