import React, { Component } from "react";
import Carousel from 'react-bootstrap/Carousel';
import { TableCard } from "../TableCard";
import { BookingApi } from "../../services/booking/BookingApi";
import Container from "react-bootstrap/Container";

type SliderState = {
    restaurants: any[]
};

export class Slider extends Component<{}, SliderState> {
    constructor(props: any) {
        super(props);

        this.state = {
            restaurants: []
        };
    }

    componentDidMount() {
        this.getRestaurants();
    }


    private getRestaurants() {
        const bookingApi = new BookingApi('');
        bookingApi.listRestaurants().then(response => {
            response.json().then(data => {
                const restaurantsData = data.data;
                console.log(restaurantsData)
                const filteredRestaurants = restaurantsData.filter((r: any) => r.tables.length > 0);
                this.setState({ restaurants: filteredRestaurants });
            });
        });

    }

    render() {
        const restaurants = this.state.restaurants;
        return(
            <Container>
                {restaurants.map((restaurant: any) =>

                    <Carousel className="p-3 container" controls={false} key={restaurant.guid}>
                        {restaurant.tables.map((table: any) =>
                            <Carousel.Item key={table.guid}>
                                <TableCard guid={table.guid} positionName={table.positionName} />
                            </Carousel.Item>
                        )};
                    </Carousel>
                )}
            </Container>
        );
    }
}
