import React, { Component } from "react";
import { BrowserRouter, NavLink, Switch, Route } from 'react-router-dom';
import Table from 'react-bootstrap/Table';
import { BookingApi } from "../../services/booking/BookingApi";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Nav from 'react-bootstrap/Nav';
import Form from 'react-bootstrap/Form';
import Button from "react-bootstrap/Button";

type BookingState = {
    restaurants: any[],
    reservations: any[],
    tables: any[],
    open: string,
    close: string,
    name: string,
    isDefaultActive: boolean;
};

export class Booking extends Component<{}, BookingState> {
    constructor(props: any) {
        super(props);

        this.state = {
            restaurants: [],
            reservations: [],
            tables: [],
            open: '',
            close: '',
            name: '',
            isDefaultActive: true
        };
    }

    public componentDidMount() {
        this.getRestaurants();
    }

    public activeLink(isActive: boolean) {
        this.setState({ isDefaultActive: isActive });
    }

    public getRestaurants() {
        const bookingApi = new BookingApi('');
        bookingApi.listRestaurants().then(response => {
            response.json().then(data => {
                this.setState({ restaurants: data.data });
            });
        });

        // bookingApi.listTables().then(response => {
        //     response.json().then(data => {
        //         this.setState({ tables: data.data });
        //     });
        // });

        // bookingApi.listReservations().then(response => {
        //     response.json().then(data => {
        //         this.setState({ reservations: data.data });
        //     });
        // });
    }

    handleDataChange(event: any) {
        console.log(event);
        // this.setState({value: event.target.value});
    }

    public createRestaurant(event: any) {
        console.log(this.state);
        event.preventDefault();
        // bookingApi.createRestaurant()
    }

    render() {
        const restaurants = this.state.restaurants;
        return(
            <BrowserRouter>
                <Container>
                    <Row>
                        <Col sm={2}>
                        <Nav variant="pills" defaultActiveKey="/list-restaurants" className="flex-column">
                            <Nav.Link as={NavLink} active={this.state.isDefaultActive} to="/list-restaurants" onClick={() => this.activeLink(true)}>List restaurants</Nav.Link>
                            <Nav.Link as={NavLink} to="/add-restaurants" onClick={() => this.activeLink(false)} >Add restaurant</Nav.Link>
                        </Nav>
                        </Col>
                        <Col sm={10}>
                            <Switch>
                                <Route path={["/list-restaurants", "/booking"]}>
                                    <Table responsive>
                                        <thead>
                                            <tr>
                                            <th>Restaurant</th>
                                            <th>Business hours</th>
                                            <th>Actions</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {restaurants.map((restaurant: any) =>
                                                <tr key={restaurant.guid}>
                                                    <td>{restaurant.name}</td>
                                                    <td>From {restaurant.opensAt} to {restaurant.closesAt} </td>
                                                    <td>{restaurant.guid} </td>
                                                </tr>
                                            )}
                                        </tbody>
                                    </Table>
                                </Route>
                                <Route path="/add-restaurants">
                                    <Form onSubmit={this.createRestaurant}>
                                        <Form.Group controlId="name">
                                            <Form.Label>Restaurant name</Form.Label>
                                            <Form.Control value={this.state.name} placeholder="Restaurant name" required />
                                        </Form.Group>

                                        <Form.Group controlId="business-hour">
                                            <Form.Label>Open</Form.Label>
                                            <Form.Control as="select" value={this.state.open} required>
                                                <option></option>
                                                <option>...</option>
                                            </Form.Control>
                                            <Form.Label>Close</Form.Label>
                                            <Form.Control as="select" value={this.state.close} required>
                                                <option></option>
                                                <option>...</option>
                                            </Form.Control>
                                        </Form.Group>
                                        <Button variant="primary" type="submit">
                                            Create
                                        </Button>
                                    </Form>
                                </Route>
                            </Switch>
                        </Col>
                    </Row>
                </Container>
            </BrowserRouter>
        );
    }
}
