import React, { Component } from "react";
import Button from "react-bootstrap/Button";
import Modal from 'react-bootstrap/Modal';

type ModalProps = {
    showModal: boolean,
    message: string,
    status: string
};

type ModalStates = {
    showModal: boolean
}

export class GenericModal extends Component<ModalProps, ModalStates> {
    constructor(props: ModalProps) {
        super(props);

        this.state = {
            showModal: props.showModal
        };

        this.openModal = this.openModal.bind(this);

        if (this.props.showModal) {
            this.openModal();
        }
    }

    public openModal() {
        this.setState({ showModal: true });
    }

    public closeModal() {
        this.setState({ showModal: false });
    }

    render() {
        console.log(this.state.showModal)
        return(
            <Modal show={this.state.showModal} onHide={this.closeModal}>
                <Modal.Header className="alert-success" closeButton>
                    <Modal.Title>{this.props.status}</Modal.Title>
                </Modal.Header>
                    <Modal.Body>{this.props.message}</Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={this.closeModal}>Ok</Button>
                </Modal.Footer>
            </Modal>
        );
    }
}
